import React, { useState } from "react";
import "../Assignments.css"; // Adjust path if needed

function Assignments() {
  const [newAssignment, setNewAssignment] = useState({
    driver_id: "",
    truck_id: "",
    start_date: "",
    end_date: "",
    status: "Pending",
  });

  const handleInputChange = (e) => {
    setNewAssignment({ ...newAssignment, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("New Assignment:", newAssignment);
    alert("Assignment created! (This is just a placeholder action)");
    setNewAssignment({
      driver_id: "",
      truck_id: "",
      start_date: "",
      end_date: "",
      status: "Pending",
    });
  };

  return (
    <div className="assignments-container">
      <h2>Create Assignment</h2>
      <form className="assignment-form" onSubmit={handleSubmit}>
        <select
          name="driver_id"
          value={newAssignment.driver_id}
          onChange={handleInputChange}
          required
        >
          <option value="">Select Driver</option>
          <option value="1">John Doe</option>
          <option value="2">Jane Smith</option>
        </select>

        <select
          name="truck_id"
          value={newAssignment.truck_id}
          onChange={handleInputChange}
          required
        >
          <option value="">Select Truck</option>
          <option value="101">Volvo FH16</option>
          <option value="102">Scania R500</option>
        </select>

        <input
          type="date"
          name="start_date"
          value={newAssignment.start_date}
          onChange={handleInputChange}
          required
        />

        <input
          type="date"
          name="end_date"
          value={newAssignment.end_date}
          onChange={handleInputChange}
          required
        />

        <select
          name="status"
          value={newAssignment.status}
          onChange={handleInputChange}
          required
        >
          <option value="Pending">Pending</option>
          <option value="Ongoing">Ongoing</option>
          <option value="Completed">Completed</option>
        </select>

        <button type="submit" className="add-assignment-btn">
          Assign Driver
        </button>
      </form>
    </div>
  );
}

export default Assignments;
