import React, { useState } from "react";
import "../Assignments.css";

function Assignments() {
  const [assignments, setAssignments] = useState([
    { id: 1, driver_name: "John Doe", truck_model: "Volvo", truck_plate: "ABC-123", start_date: "2025-03-20", end_date: "2025-03-30", status: "Ongoing" },
    { id: 2, driver_name: "Jane Smith", truck_model: "Scania", truck_plate: "XYZ-789", start_date: "2025-03-22", end_date: "2025-04-05", status: "Pending" }
  ]);

  const [drivers] = useState([
    { id: 1, name: "John Doe" },
    { id: 2, name: "Jane Smith" }
  ]);

  const [trucks] = useState([
    { id: 1, model: "Volvo", plate_number: "ABC-123" },
    { id: 2, model: "Scania", plate_number: "XYZ-789" }
  ]);

  const [newAssignment, setNewAssignment] = useState({
    driver_id: "",
    truck_id: "",
    start_date: "",
    end_date: "",
    status: "Pending"
  });

  const userRole = localStorage.getItem("role");
  const isAdmin = userRole === "admin";

  const handleInputChange = (e) => {
    setNewAssignment({ ...newAssignment, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const selectedDriver = drivers.find(d => d.id === parseInt(newAssignment.driver_id));
    const selectedTruck = trucks.find(t => t.id === parseInt(newAssignment.truck_id));

    if (selectedDriver && selectedTruck) {
      setAssignments([
        ...assignments,
        {
          id: assignments.length + 1,
          driver_name: selectedDriver.name,
          truck_model: selectedTruck.model,
          truck_plate: selectedTruck.plate_number,
          start_date: newAssignment.start_date,
          end_date: newAssignment.end_date,
          status: newAssignment.status
        }
      ]);
    }
  };

  return (
    <div className="assignments-list">
      <h2>Assignments</h2>

      {isAdmin && (
        <form className="assignment-form" onSubmit={handleSubmit}>
          <select name="driver_id" value={newAssignment.driver_id} onChange={handleInputChange} required>
            <option value="">Select Driver</option>
            {drivers.map(driver => (
              <option key={driver.id} value={driver.id}>{driver.name}</option>
            ))}
          </select>

          <select name="truck_id" value={newAssignment.truck_id} onChange={handleInputChange} required>
            <option value="">Select Truck</option>
            {trucks.map(truck => (
              <option key={truck.id} value={truck.id}>{truck.model} ({truck.plate_number})</option>
            ))}
          </select>

          <input type="date" name="start_date" value={newAssignment.start_date} onChange={handleInputChange} required />
          <input type="date" name="end_date" value={newAssignment.end_date} onChange={handleInputChange} required />

          <select name="status" value={newAssignment.status} onChange={handleInputChange} required>
            <option value="Pending">Pending</option>
            <option value="Ongoing">Ongoing</option>
            <option value="Completed">Completed</option>
          </select>

          <button type="submit" className="add-assignment-btn">Assign Driver</button>
        </form>
      )}

      {assignments.length === 0 ? (
        <p className="no-data">No assignments available.</p>
      ) : (
        <table className="assignments-table">
          <thead>
            <tr>
              <th>ID</th>
              <th>Driver</th>
              <th>Truck</th>
              <th>Start Date</th>
              <th>End Date</th>
              <th>Status</th>
            </tr>
          </thead>
          <tbody>
            {assignments.map(assignment => (
              <tr key={assignment.id}>
                <td>{assignment.id}</td>
                <td>{assignment.driver_name}</td>
                <td>{assignment.truck_model} ({assignment.truck_plate})</td>
                <td>{assignment.start_date}</td>
                <td>{assignment.end_date}</td>
                <td>{assignment.status}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default Assignments;
