import React, { useEffect, useState } from "react";
import axios from "axios";
import "..Assignments.css";

function Assignments() {
  const [assignments, setAssignments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchAssignments();
  }, []);

  const fetchAssignments = async () => {
    try {
      const response = await axios.get("http://localhost:5000/assignments");
      setAssignments(response.data);
    } catch (err) {
      setError("Failed to load assignments.");
      console.error("Error fetching assignments:", err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="assignments-container">
      <h2>Assignments</h2>

      {loading && <p className="loading">Loading assignments...</p>}
      {error && <p className="error">{error}</p>}

      {!loading && !error && assignments.length === 0 && (
        <p className="no-data">No assignments available.</p>
      )}

      {!loading && !error && assignments.length > 0 && (
        <table className="assignments-table">
          <thead>
            <tr>
              <th>Assignment ID</th>
              <th>Driver</th>
              <th>Truck</th>
              <th>Start Date</th>
              <th>End Date</th>
              <th>Status</th>
            </tr>
          </thead>
          <tbody>
            {assignments.map((assignment) => (
              <tr key={assignment.id}>
                <td>{assignment.id}</td>
                <td>{assignment.driver_name}</td>
                <td>{assignment.truck_model} ({assignment.truck_plate})</td>
                <td>{assignment.start_date}</td>
                <td>{assignment.end_date}</td>
                <td>{assignment.status}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default Assignments;
