import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, Link, Navigate } from "react-router-dom";
import Trucks from "./pages/Trucks";
import Assignments from "./pages/Assignments";
import "./App.css";

function App() {
  // Get user role from localStorage
  const [userRole, setUserRole] = useState(localStorage.getItem("role"));
  const isAdmin = userRole === "admin";

  // Debugging logs
  console.log("App is rendering...");
  console.log("User role:", userRole);
  console.log("Is Admin:", isAdmin);

  // Update role when localStorage changes
  useEffect(() => {
    const handleStorageChange = () => {
      setUserRole(localStorage.getItem("role"));
    };

    window.addEventListener("storage", handleStorageChange);
    return () => {
      window.removeEventListener("storage", handleStorageChange);
    };
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("role");
    localStorage.removeItem("token");
    setUserRole(null); // Clear state
    window.location.href = "/login"; // Redirect to login
  };

  return (
    <Router>
      <div className="App">
        {/* Navigation Bar */}
        <nav className="navbar">
          <div className="nav-left">
            <ul className="nav-links">
              <li><Link to="/">Home</Link></li>
              {isAdmin && <li><Link to="/trucks">Trucks</Link></li>}
              <li><Link to="/assignments">Assignments</Link></li>
            </ul>
          </div>
          <div className="nav-right">
            <ul className="nav-links">
              <li><button className="logout-btn" onClick={handleLogout}>Logout</button></li>
            </ul>
          </div>
        </nav>

        {/* Page Content */}
        <div className="content">
          <Routes>
            <Route path="/" element={<h1>Welcome to Globe Trans Truck Management</h1>} />
            {isAdmin && <Route path="/trucks" element={<Trucks />} />}
            <Route path="/assignments" element={<Assignments />} />
            <Route path="/login" element={<h1>Login Page (Placeholder)</h1>} />
            <Route path="*" element={<Navigate to="/" />} />
          </Routes>
        </div>
      </div>
    </Router>
  );
}

export default App;
