import React, { useEffect, useState } from "react";
import "../Trucks.css";

function Trucks() {
  const [trucks, setTrucks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [message, setMessage] = useState("");
  const [newTruck, setNewTruck] = useState({ id: "", plate_number: "", model: "", status: "Available" });

  const userRole = localStorage.getItem("role");
  const isAdmin = userRole === "admin";

  useEffect(() => {
    // Simulated API request with mock data
    setTimeout(() => {
      setTrucks([
        { id: 1, plate_number: "ABC123", model: "Volvo FH", status: "Available", created_at: "2025-03-19T10:00:00" },
        { id: 2, plate_number: "XYZ789", model: "Scania R450", status: "In Use", created_at: "2025-03-18T15:30:00" }
      ]);
      setLoading(false);
    }, 1000);
  }, []);

  const handleInputChange = (e) => {
    setNewTruck(prevState => ({
      ...prevState,
      [e.target.name]: e.target.value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setMessage("");

    const truckToAdd = {
      ...newTruck,
      id: trucks.length ? trucks[trucks.length - 1].id + 1 : 1, // Auto-increment ID
      created_at: new Date().toISOString(),
    };

    setTrucks([...trucks, truckToAdd]);
    setNewTruck({ id: "", plate_number: "", model: "", status: "Available" });
    setMessage("Truck added successfully!");

    setTimeout(() => setMessage(""), 3000);
  };

  return (
    <div className="trucks-container">
      <h2>Trucks</h2>

      {/* Truck Form - Only visible to Admin */}
      {isAdmin && (
        <form className="truck-form" onSubmit={handleSubmit}>
          <label>ID:</label>
          <input type="text" name="id" placeholder="ID (Auto-generated)" value={newTruck.id} disabled />

          <label>Plate Number:</label>
          <input type="text" name="plate_number" placeholder="Plate Number" value={newTruck.plate_number} onChange={handleInputChange} required />

          <label>Model:</label>
          <input type="text" name="model" placeholder="Truck Model" value={newTruck.model} onChange={handleInputChange} required />

          <label>Status:</label>
          <select name="status" value={newTruck.status} onChange={handleInputChange} required>
            <option value="Available">Available</option>
            <option value="In Use">In Use</option>
            <option value="Maintenance">Maintenance</option>
          </select>

          <button type="submit">Add Truck</button>
        </form>
      )}

      {message && <p className="success-message">{message}</p>}
      {loading ? <p>Loading...</p> : (
        <table>
          <thead>
            <tr>
              <th>ID</th><th>Plate Number</th><th>Model</th><th>Status</th><th>Created At</th>
            </tr>
          </thead>
          <tbody>
            {trucks.map(truck => (
              <tr key={truck.id}>
                <td>{truck.id}</td>
                <td>{truck.plate_number}</td>
                <td>{truck.model}</td>
                <td>{truck.status}</td>
                <td>{new Date(truck.created_at).toLocaleString()}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default Trucks;
