import React, { useEffect, useState } from "react";
import axios from "axios";
import "../Trucks.css";

function Trucks() {
  const [trucks, setTrucks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [newTruck, setNewTruck] = useState({ model: "", plate_number: "" });

  const userRole = localStorage.getItem("role");
  const isAdmin = userRole === "admin";

  useEffect(() => {
    axios.get("http://localhost:5000/trucks")
      .then(response => {
        setTrucks(response.data);
        setLoading(false);
      })
      .catch(error => {
        setError("Failed to load trucks.");
        setLoading(false);
      });
  }, []);

  const handleInputChange = (e) => {
    setNewTruck({ ...newTruck, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    axios.post("http://localhost:5000/trucks", newTruck)
      .then(() => {
        setTrucks([...trucks, newTruck]);
        setNewTruck({ model: "", plate_number: "" });
      })
      .catch(error => setError("Failed to add truck."));
  };

  return (
    <div className="trucks-list">
      <h2>Trucks</h2>

      {isAdmin && (
        <form className="truck-form" onSubmit={handleSubmit}>
          <input type="text" name="model" placeholder="Truck Model" value={newTruck.model} onChange={handleInputChange} required />
          <input type="text" name="plate_number" placeholder="Plate Number" value={newTruck.plate_number} onChange={handleInputChange} required />
          <button type="submit" className="add-truck-btn">Add Truck</button>
        </form>
      )}

      {loading && <p className="loading">Loading trucks...</p>}
      {error && <p className="error">{error}</p>}

      {!loading && !error && (
        <table className="trucks-table">
          <thead>
            <tr>
              <th>ID</th>
              <th>Model</th>
              <th>Plate Number</th>
            </tr>
          </thead>
          <tbody>
            {trucks.map(truck => (
              <tr key={truck.id}>
                <td>{truck.id}</td>
                <td>{truck.model}</td>
                <td>{truck.plate_number}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default Trucks;
