import React, { useEffect, useState } from "react";
import axios from "axios";
import "../Drivers.css";

function Drivers() {
  const [drivers, setDrivers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [newDriver, setNewDriver] = useState({ name: "", license_number: "" });

  const userRole = localStorage.getItem("role");
  const isAdmin = userRole === "admin";

  useEffect(() => {
    axios.get("http://localhost:5000/drivers")
      .then(response => {
        setDrivers(response.data);
        setLoading(false);
      })
      .catch(error => {
        setError("Failed to load drivers.");
        setLoading(false);
      });
  }, []);

  const handleInputChange = (e) => {
    setNewDriver({ ...newDriver, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    axios.post("http://localhost:5000/drivers", newDriver)
      .then(() => {
        setDrivers([...drivers, newDriver]);
        setNewDriver({ name: "", license_number: "" });
      })
      .catch(error => setError("Failed to add driver."));
  };

  return (
    <div className="drivers-list">
      <h2>Drivers</h2>

      {isAdmin && (
        <form className="driver-form" onSubmit={handleSubmit}>
          <input type="text" name="name" placeholder="Driver Name" value={newDriver.name} onChange={handleInputChange} required />
          <input type="text" name="license_number" placeholder="License Number" value={newDriver.license_number} onChange={handleInputChange} required />
          <button type="submit" className="add-driver-btn">Add Driver</button>
        </form>
      )}

      {loading && <p className="loading">Loading drivers...</p>}
      {error && <p className="error">{error}</p>}

      {!loading && !error && (
        <table className="drivers-table">
          <thead>
            <tr>
              <th>ID</th>
              <th>Name</th>
              <th>License Number</th>
            </tr>
          </thead>
          <tbody>
            {drivers.map(driver => (
              <tr key={driver.id}>
                <td>{driver.id}</td>
                <td>{driver.name}</td>
                <td>{driver.license_number}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default Drivers;
