// Assignments Page
import "../Assignments.css";
function Assignments() {
  const [assignments, setAssignments] = useState([]);
  useEffect(() => {
    axios.get("http://localhost:5000/assignments")
      .then(response => setAssignments(response.data))
      .catch(() => alert("Failed to load assignments."));
  }, []);
  return (
    <div className="assignments-list">
      <h2>Assignments</h2>
      <table>
        <thead>
          <tr><th>ID</th><th>Driver</th><th>Truck</th><th>Start Date</th><th>End Date</th><th>Status</th></tr>
        </thead>
        <tbody>
          {assignments.map(assignment => (
            <tr key={assignment.id}><td>{assignment.id}</td><td>{assignment.driver_name}</td><td>{assignment.truck_model}</td><td>{assignment.start_date}</td><td>{assignment.end_date}</td><td>{assignment.status}</td></tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
export default Assignments;
